glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
in vec4 a_color;
out vec4 v_color;
#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif
void main () {
  vec4 pos = vec4(a_position, 1);
  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif
  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif
  v_color = a_color;
  gl_Position = pos;
}

glsl3.frag:
precision highp float;
#if USE_ALPHA_TEST
  uniform ALPHA_TEST {
    float alphaThreshold;
  };
#endif
in vec4 v_color;
#if USE_TEXTURE
in vec2 v_uv0;
uniform sampler2D texture;
uniform sampler2D textureScratches;
uniform DustSpeed{
  float dustXSpeed;
  float dustYSpeed;
};
uniform ScratchesSpeed {
  float scratchesXSpeed;
  float scratchesYSpeed;
};
uniform Data{
  float randomValue;
  float time;
};
#endif
void main () {
  #if USE_TEXTURE
    vec2 dustUV = vec2(v_uv0.x + (randomValue * (sin(time) / 2. * dustXSpeed)),  v_uv0.y + (randomValue * (sin(time) / 2. * dustYSpeed)));
    vec2 scratchesUV = vec2(v_uv0.x + (randomValue * (sin(time) / 1. * scratchesXSpeed)),  v_uv0.y + (randomValue * (sin(time)) / 1. * scratchesYSpeed));
    gl_FragColor = texture2D(texture, dustUV) + texture2D(textureScratches, scratchesUV);
  #endif
}

