glsl1.frag:
precision highp float;
#if USE_ALPHA_TEST
#endif
varying vec4 v_color;
#if USE_TEXTURE
varying vec2 v_uv0;
uniform sampler2D texture;
uniform sampler2D textureScratches;
uniform float dustXSpeed;
uniform float dustYSpeed;
uniform float scratchesXSpeed;
uniform float scratchesYSpeed;
